export function getAreaIntName(name) {
  let hash = 0;

  for (let i=0; i<name.length; i++) {
    let c = name.charCodeAt(i);

    if (i % 2 == 0) {
      hash += c<<8;
      hash *= 13;
      hash = hash & ((1<<15)-1);
    } else {
      hash += c;
    }
  }

  return hash;
}
