  popup(owning_node, elem_or_x, y, closeOnMouseOut=true, popupDelay=250) {
    let ret = this._popup(...arguments);

    if (popupDelay === 0) {
      return ret;
    }

    let z = ret.style["z-index"];

    ret.style["z-index"] = "-10";

    let cb = () => {
      let rect = ret.getClientRects()[0];
      let size = this.size;

      if (!rect) {
        this.doOnce(cb);
