  _calcSizeKey(w, h, x, y, dpi, scale) {
    if (arguments.length !== 6) {
      throw new Error("eek");
    }

    let s = "";
    for (let i=0; i<arguments.length; i++) {
      s += arguments[i].toFixed(0)+":";
    }

    return s;
