    constructor(iter, tag, owner) {
      this.iter = iter;
      this.tag = tag;
      this.owner = owner;
      this.dead = false;
      this.status = new JobStatus();

      this.thens = [];
      this.catcher = undefined;
    }

    then(cb) {
      this.thens.push(cb);
    }

    catch(cb) {
      this.catcher = cb;
    }

    stop() {
      this.dead = true;
    }

    next() {
      if (this.dead) {
        return this.dead;
      }

      var ret = this.iter.next();
      if (ret.done) {
        this.dead = {done: true, value : undefined};
      }

      return ret;
