    newNode(min, max) {
      var maxnodes = this.data.length / TOTN;
      
      if (this.usednodes >= maxnodes) {
        let newdata = new Float64Array(this.data.length*2);
        let data = this.data;
        let ilen = data.length;
        
        for (let i=0; i<ilen; i++) {
          newdata[i] = data[i];
        }
        
        this.data = newdata;
      }
      
      var ni = this.usednodes*TOTN;
      var data = this.data;
      
      for (let j=ni; j<ni+TOTN; j++) {
        data[j] = 0;
      }
      
      data[ni+NXMIN] = min[0];
      data[ni+NYMIN] = min[1];
      data[ni+NZMIN] = min[2];
      
      data[ni+NXMAX] = max[0];
