export const CurveConstructors = {
  [CurveTypes.BSPLINE]    : BSplineCurve,
  [CurveTypes.CUSTOM]     : CustomCurve,
  [CurveTypes.GUASSIAN]   : GuassianCurve
};

export class Curve {
  constructor(setting_id) {
    this.generators = [];
    this.setting_id = setting_id;
    this.uidata = undefined;
    this._fastmode = false;
    
    if (setting_id === undefined) {
      throw new Error("setting_id cannot be undefined");
    }
    
    for (let k in CurveConstructors) {
      let gen = new CurveConstructors[k];
      
      this.generators.push(gen);
    }
    
    this.generators.active = this.generators[0];
  }
  
  redraw() {
    if (this._redraw_hook !== undefined) {
      this._redraw_hook();
    }
  }
  
  /*
  new isolation strategy I'm trying.  Instead of keeping reference
  to CurveWidget in Curve and CurveDataType, I'll have CurveWidget feed
  in special callback functions.  
  
  this is necessary for redrawing and auto saving.
  */
  setRedrawHook(cb) {
    for (let gen of this.generators) {
      gen.setRedrawHook(cb);
    }
    
    this._redraw_hook = cb;
  }
  
  doSave() {
    if (this._save_hook) {
      this._save_hook();
    }
  }
  
  setSaveHook(cb) {
    this._save_hook = cb;
    
    for (let gen of this.generators) {
      gen.setSaveHook(cb);
    }
    
    return this;
  }
  
  get fastmode() {
    return this._fastmode;
  }
  
  set fastmode(val) {
    this._fastmode = val;
    
    for (let gen of this.generators) {
      gen.fastmode = val;
    }
  }
  
  toJSON() {
    let ret = {
      is_new_curve     : true,
      setting_id       : this.setting_id,
      generators       : [],
      version          : CURVE_VERSION,
      active_generator : this.generators.indexOf(this.generators.active)
    };
    
    for (let gen of this.generators) {
      ret.generators.push(gen.toJSON());
    }
    
    ret;//return Object.assign(super.toJSON(), ret);
  }
  
  getGenerator(type) {
    for (let gen of this.generators) {
      if (gen.type === type) {
        return gen;
      }
    }
    
    throw new Error("Unknown generator " + type + ".");
  }
  
  switchGenerator(type) {
    let gen = this.getGenerator(type);
    
    if (gen !== this.generators.active) {
      let old = this.generators.active;

      this.generators.active = gen;
      
      if (this.uidata !== undefined) {
        let ud = this.uidata;
        old.killGUI(ud.dom, ud.gui, ud.canvas, ud.g, ud.draw_trans);
      }
      
      old.onInactive(this);
      gen.onActive(this);
      
      if (this.uidata !== undefined) {
        let ud = this.uidata;
        gen.makeGUI(ud.dom, ud.gui, ud.canvas, ud.g, ud.draw_trans);
      }
    }
    
    return gen;
  }
  
  destroy() {
    if (this.uidata !== undefined) {
      let ud = this.uidata;
      
      this.killGUI(ud.dom, ud.gui, ud.canvas, ud.g, ud.draw_trans);
      
      for (let gen of this.generators) {
        gen.destroy();
      }
    }
    
    return this;
  }
  
  loadJSON(obj) {
    if (obj.is_new_curve === undefined) {
      let hasGUI = this.uidata !== undefined;
      let olduidata = this.uidata;
      
      if (hasGUI) {
        let ud = olduidata;
        this.killGUI(ud.dom, ud.gui, ud.canvas, ud.g, ud.draw_trans);
      }

      let gen = this.switchGenerator(CurveTypes.BSPLINE);
      
      this.generators.active = gen;
      
      obj.type = CurveTypes.BSPLINE;
      
      gen.reset();
      gen.loadJSON(obj);
      
      if (hasGUI) {
        let ud = olduidata;
        this.makeGUI(ud.dom, ud.gui, ud.canvas, ud.g, ud.draw_trans);
      }

      this.update();
      this.redraw();
      
      return this;
    }
    
    let hasGUI = this.uidata !== undefined;
    let olduidata = this.uidata;
    
    if (hasGUI) {
      let ud = olduidata;
      this.killGUI(ud.dom, ud.gui, ud.canvas, ud.g, ud.draw_trans);
    }
    
    if (this.setting_id === undefined) {
      console.warn("Warning, setting setting_id in Curve.loadJSON(); you forgot to pass it to the constructor");
      this.setting_id = obj.setting_id;
    }
    
    for (let gen of obj.generators) {
      if (!(gen.type in CurveConstructors)) {
        throw new Error("Bad generator type " + gen.type);
        //console.warn("Bad generator type", gen.type, gen);
        continue;
      }
      
      this.getGenerator(gen.type).loadJSON(gen).update();
    }
    
    this.generators.active = this.generators[obj.active_generator];

    if (hasGUI) {
      let ud = olduidata;
      this.makeGUI(ud.dom, ud.gui, ud.canvas, ud.g, ud.draw_trans);
    }
    
    this.update();
    this.redraw();
    
    return this;
  }
  
  evaluate(s) {
    return this.generators.active.evaluate(s);
  }
  
  derivative(s) {
    return this.generators.active.derivative(s);
  }
  
  derivative2(s) {
    return this.generators.active.derivative2(s);
  }
  
  inverse(s) {
    return this.generators.active.inverse(s);
  }
  
  reset() {
    this.generators.active.reset();
  }
  
  update() {
    return this.generators.active.update();
  }
  
  destroy_all_settings() {
    delete localStorage[this.setting_id];
    
    return this;
  }
  
  makeGUI(dom, gui, canvas, g, draw_transform) {
    this.uidata = {
      dom        : dom,
      gui        : gui,
      canvas     : canvas,
      g          : g,
      draw_trans : draw_transform
    };

    if (this.generators.active.hasGUI) {
      this.generators.active.killGUI(dom, gui, canvas, g, draw_transform);
    }
    
    let uinames = {};
    for (let k in CurveTypes) {
      let v = CurveTypes[k];

      k = k[0].toUpperCase() + k.slice(1, k.length).toLowerCase();
      k = k.replace(/_/g, " ");

      uinames[k] = v;
    }

    //listenum(path, name, enummap, defaultval, callback)
    this.uidata.listenum = gui.listenum(undefined, "Type", uinames, this.generators.active.type, (type) => {
      this.switchGenerator(type);
      this.doSave();
      this.redraw();
    });
    
    this.generators.active.makeGUI(dom, gui, canvas, g, draw_transform);
    
    return this;
  }
  
  get hasGUI() {
    return this.uidata !== undefined;
  }

  killGUI(dom, gui, canvas, g, draw_transform) {
    this.uidata.listenum.remove();

    for (let gen of this.generators) {
      this.generators.active.killGUI(dom, gui, canvas, g, draw_transform);
    }
    
    this.uidata = undefined;
    
    return this;
  }
  
  draw(canvas, g, draw_transform) {
    if (this.uidata === undefined) {
      return;
    }
    
    this.uidata.canvas = canvas;
    this.uidata.g = g;
    this.uidata.draw_trans = draw_transform;
    
    var w=canvas.width, h=canvas.height;
    
    g.save();
    
    let sz = draw_transform[0], pan = draw_transform[1];
    
    g.beginPath();
    g.moveTo(-1, 0);
    g.lineTo(1, 0);
    g.strokeStyle = "red";
    g.stroke();
    
    g.beginPath();
    g.moveTo(0, -1);
    g.lineTo(0, 1);
    g.strokeStyle = "green";
    g.stroke();
    
    //g.rect(0, 0, 1, 1);
    //g.fillStyle = "rgb(50, 50, 50)";
    //g.fill();
    
    var f=0, steps=64;
    var df = 1/(steps-1);
    var w = 6.0/sz;
    
    let curve = this.generators.active;
    
    g.beginPath();
    for (var i=0; i<steps; i++, f += df) {
      var val = curve.evaluate(f);
      
      (i==0 ? g.moveTo : g.lineTo).call(g, f, val, w, w);
    }
    
    g.strokeStyle = "grey";
    g.stroke();
    
    if (this.overlay_curvefunc !== undefined) {
      g.beginPath();
      f = 0.0;
      
      for (var i=0; i<steps; i++, f += df) {
        var val = this.overlay_curvefunc(f);
        
        (i==0 ? g.moveTo : g.lineTo).call(g, f, val, w, w);
      }
      
      g.strokeStyle = "green";
      g.stroke();
    }
    
    this.generators.active.draw(canvas, g, draw_transform);
    
    g.restore();
    return this;
  }
}

export class CurveWidget {
  constructor(setting_id) {
    this.curve = new Curve(setting_id);
    this.setting_id = setting_id;
    
    this._closed = false;
    this.mpos = [0, 0];
    
    this.domparent = undefined;
    this.canvas = undefined;
    this.g = undefined;
    
    this.overlay_curvefunc = undefined;
    
    this.curve.setSaveHook(this.save.bind(this));
    this.curve.setRedrawHook(this.redraw.bind(this));
  }
  
  redraw() {
    this.draw();
  }
  
  save() {
    //console.warn("Temporarily disabled curve saving!");
    localStorage[this.setting_id] = JSON.stringify(this.curve);
    //window[this.setting_id.toUpperCase()] = this.curve;
  }
      
  //default_preset is optional, undefined
  load(default_preset) {
    if (this.setting_id in localStorage) {
      this.curve.loadJSON(JSON.parse(localStorage[this.setting_id]));
    } else if (default_preset !== undefined) {
      this.curve.loadJSON(default_preset);
    }
    
    this.curve.update();
    this.draw();
  }
  
  bind(dom, gui) {
    this.canvas = document.createElement("canvas");
    this.canvas.width = 200;
    this.canvas.height = 200;
    this.g = this.canvas.getContext("2d");
    
    this.gui = gui;
    this.domparent = dom;
    
    this.canvas["class"] = "closed";
    this.canvas.style["class"] = "closed";
    
    dom.appendChild(this.canvas);
    
    this.curve.makeGUI(dom, gui, this.canvas, this.g, this.draw_transform());
  }
  
  draw_transform(g) {
    var sz = Math.min(this.canvas.width, this.canvas.height);
    sz *= 0.8;
    
    var pan = [0.1, -1.1];
    
    if (g != undefined) {
      g.lineWidth /= sz;
      g.scale(sz, -sz);
      g.translate(pan[0], pan[1]);
    }
    
    return [sz, pan];
  }
  
  get closed() {
    return this._closed;
  }
  
  set closed(val) {
    //this.canvas.style["visibility"] = val ? "collapse" : "visible"
    if (val && !this._closed) {
      this.curve.killGUI(this.domparent, this.gui, this.canvas, this.g, this.draw_transform());
      this.canvas.remove();
    } else if (!val && this._closed) {
      this.domparent.appendChild(this.canvas);
      this.curve.makeGUI(this.domparent, this.gui, this.canvas, this.g, this.draw_transform());
      this.redraw();
    }
    
    this._closed = val;
  }
  
  draw() {
    if (this.canvas === undefined) {
      return;
    }
    
    var g = this.g;
    var w=this.canvas.width, h=this.canvas.height;
    
    g.clearRect(0, 0, w, h);
    
    g.fillStyle = "rgb(50, 50, 50)";
    g.beginPath();
    g.rect(0, 0, w, h);
    g.fill();
    
    g.save();
    
    var trans = this.draw_transform(g);
    
    //XXX draw min/max bounds here
    g.strokeStyle = "rgb(100,100,100)";
    g.beginPath();
    g.rect(0, 0, 1, 1);
    g.stroke();
    
    this.curve.draw(this.canvas, this.g, trans);
    
    g.restore();
  }
}

import {AreaFlags} from "../screen/ScreenArea.js";
import {keymap} from "../util/simple_events.js";

export function makePopupArea(area_class, screen, args={}) {
  let sarea = document.createElement("screenarea-x");

  let width = args.width || (screen.size[0]*0.7);
  let height = args.height || (screen.size[1]*0.7);
  let addEscapeKeyHandler = args.addEscapeKeyHandler !== undefined ? args.addEscapeKeyHandler : true;

  sarea.ctx = screen.ctx;
  sarea.size[0] = width;
  sarea.size[1] = height;
  sarea.pos[0] = 100;
  sarea.pos[1] = 100;

  sarea.pos[0] = Math.min(sarea.pos[0], screen.size[0] - sarea.size[0] - 2);
  sarea.pos[1] = Math.min(sarea.pos[1], screen.size[1] - sarea.size[1] - 2);

  sarea.switch_editor(area_class);

  sarea.style["background-color"] = sarea.getDefault("DefaultPanelBG");

  sarea.area.flag |= AreaFlags.FLOATING | AreaFlags.INDEPENDENT;

  screen.appendChild(sarea);
  sarea.setCSS();

  if (addEscapeKeyHandler) {
    sarea.on_keydown = (e) => {
      if (e.keyCode === keymap.Escape) {
        screen.removeArea(sarea);
      }
    }
  }

  sarea.bringToFront();

  return sarea;
}

import {UIBase} from "../core/ui_base.js";
import {ColumnFrame} from "../core/ui.js";
import {PropTypes, PropFlags} from "../toolsys/toolprop.js";

import {UndoFlags} from "../toolsys/simple_toolsys.js";
import {DataPath, DataTypes} from "../controller/simple_controller.js";

import * as util from '../util/util.js';
import cconst from '../config/const.js';

const LastKey = Symbol("LastToolPanelId");
let tool_idgen = 0;

/*
*
* This panel shows the most recently executed ToolOp's
* settings.  It assumes that recent toolops are accessible
* in ctx.last_tool.
* */
export class LastToolPanel extends ColumnFrame {
  constructor() {
    super();

    this._tool_id = undefined;
    this.useDataPathUndo = false;
  }

  init() {
    super.init();

    this.useDataPathUndo = false;
    this.rebuild();
  }

  rebuild() {
    let ctx = this.ctx;
    if (ctx === undefined) {
      this._tool_id = -1; //wait for .ctx
      return;
    }

    this.clear();

    this.label("Recent Command Settings");

    //don't process the root toolop
    let bad = ctx.toolstack.length === 0;
    bad = bad || ctx.toolstack[ctx.toolstack.cur].undoflag & UndoFlags.IS_UNDO_ROOT;

    if (bad) {
      this.setCSS();
      return;
    }

    let tool = ctx.toolstack[ctx.toolstack.cur];
    let def = tool.constructor.tooldef();
    let name = def.uiname !== undefined ? def.uiname : def.name;

    let panel = this.panel(def.uiname);

    let fakecls = {};
    fakecls.constructor = fakecls;

    //in theory it shouldn't matter if multiple last tool panels
    //override _last_tool, since they all access the same data
    this.ctx.state._last_tool = fakecls;
    let lastkey = tool[LastKey];

    let getTool = () => {
      let tool = this.ctx.toolstack[this.ctx.toolstack.cur];
      if (!tool || tool[LastKey] !== lastkey) {
        return undefined;
      }

      return tool;
    };

    let st = this.ctx.api.mapStruct(fakecls, true);
    let paths = [];

    function defineProp(k, key) {
      Object.defineProperty(fakecls, key, {
        get : function() {
          let tool = getTool();
          if (tool) {
            return tool.inputs[k].getValue();
          }
        },

        set : function(val) {
          let tool = getTool();
          if (tool) {
            tool.inputs[k].setValue(val);
            ctx.toolstack.rerun(tool);

            window.redraw_viewport();
          }
        }
      });
    }

    for (let k in tool.inputs) {
      let prop = tool.inputs[k];

      console.log("PROP FLAG", prop.flag, k);
      if (prop.flag & (PropFlags.PRIVATE|PropFlags.READ_ONLY)) {
        continue;
      }

      let uiname = prop.uiname !== undefined ? prop.uiname : k;

      prop.uiname = uiname;
      let apikey = k.replace(/[\t ]/g, "_");

      let dpath = new DataPath(apikey, apikey, prop, DataTypes.PROP);
      st.add(dpath);

      paths.push(dpath);

      defineProp(k, apikey);
    }

    for (let dpath of paths) {
      let path = "last_tool." + dpath.path;

      panel.label(dpath.data.uiname);
      panel.prop(path);
    }
    this.setCSS();

    console.log("Building last tool settings");
  }

  update() {
    super.update();
    let ctx = this.ctx;

    if (ctx.toolstack.length == 0) {
      return;
    }

    let tool = ctx.toolstack[ctx.toolstack.cur];
