export class RichEditor extends TextBoxBase {
  constructor() {
    super();

    this._disabled = false;
    this._value = "";

    this.textOnlyMode = false;

    this.styletag = document.createElement("style");
    this.styletag.textContent = `
      div.rich-text-editor-x {
