export function checkForTextBox(screen, x, y) {
  let elem = screen.pickElement(x, y);
  //console.log(elem, x, y);

  if (elem && elem instanceof TextBoxBase) {
    return true;
  }

  return false;
}

import '../util/ScreenOverdraw.js';
import {UIBase, Icons} from '../core/ui_base.js';
