"use strict";

import * as util from './util.js';
import * as icon_enum from './icon_enum.js';
import * as events from './events.js';
import * as toolsys from './simple_toolsys.js';
import * as ui from './ui.js';
import * as image from './image.js';
import * as FrameManager from './FrameManager.js';
import * as ScreenArea from './ScreenArea.js';
import * as ui_widgets from './ui_widgets.js';
import * as controller from './controller.js';
import * as ui_base from './ui_base.js';
import * as cconst from './const.js';
import * as config from './config.js';

let Icons = icon_enum.Icons;
ui_base.setIconMap(Icons);

import './struct.js';

let PackFlags = ui_base.PackFlags;
let SimpleContext = ui.SimpleContext;

window.STARTUP_FILE_NAME = "startup_file_pathux";

export class CanvasArea extends ScreenArea.Area {
  constructor() {
    super();

    this.canvas = document.createElement("canvas");
    this.g = this.canvas.getContext("2d");

    this.remakeUI();
    this.shadow.appendChild(this.canvas);

    let mstart = (e) => {
      let sa = this.owning_sarea;

      if (!sa) return e;

      e = events.copyMouseEvent(e);

      e.x -= sa.pos[0];
      e.y -= sa.pos[1];
      e.pageX -= sa.pos[0];
      e.pageY -= sa.pos[1];
      e.clientX -= sa.pos[0];
      e.clientY -= sa.pos[1];

      //console.log(e.x, e.y, _appstate.screen.sareas.active.pos === sa);

      return e;
    }

    this.canvas.addEventListener("mousedown", (e) => {
      e = mstart(e);
    });

    this.canvas.addEventListener("mousemove", (e) => {
      e = mstart(e);
    });

    this.canvas.addEventListener("mouseup", (e) => {
      e = mstart(e);
    });      
  }

  remakeUI() {
    if (this.ui !== undefined) {
      this.ui.remove();
      this.canvas.remove();
      this.ui = undefined;
    }

    let ui = this.ui = document.createElement("container-x");
    ui.ctx = _appstate.ctx;
    ui.style["width"] = "100%";
    //ui.style["height"] = "245px";

    this.shadow.appendChild(ui);

    let row = ui.row();

    row.tool("screen.area.split", PackFlags.USE_ICONS|PackFlags.SMALL_ICON, (cls) => new cls(_appstate.screen));
    row.tool("screen.area.split", 0, (cls) => new cls(_appstate.screen));
    let SEP = ui_widgets.Menu.SEP;

    row.menu("File", [
      ["Open", () => console.log("yay open")],
      "screen.area.split",
      "screen.area.drag",
      SEP,
      ["Close", () => console.log("yay close")],
    ]);

    row.check("EXAMPLE_OPTION", "ExampleCheck0");
    row.check("EXAMPLE_OPTION", "ExampleCheck0");
    row.check("EXAMPLE_OPTION", "ExampleCheck0");
    //*

    row.checkenum("enumval", "Enum", ui_base.PackFlags.USE_ICONS|ui_base.PackFlags.SMALL_ICON|ui_base.PackFlags.VERTICAL);
    row.listenum("enumval", "Enum");
    //*/

    row = ui.row();
    let tabs = row.tabs("right");
    tabs.style["width"] = "300px";
    tabs.style["height"] = "400px";
    tabs.float(1, 75*ui_base.UIBase.getDPI(), 7);
    //*

    let tab = tabs.tab("yay");

    //tab.float(1, 1, 7);
    //tab.slider("EXAMPLE_PARAM", "Example", 128, 1, 512, true, false);
    tab.colorPicker();

    tab = tabs.tab("Display");
    tab.slider("EXAMPLE_PARAM", "Example", 128, 1, 512, true, false);
    tab.slider("EXAMPLE_PARAM", "Example", 128, 1, 512, true, false);
    tab.slider("EXAMPLE_PARAM", "Example", 128, 1, 512, true, false);

    tabs.tab("one");
    tabs.tab("two");
    tabs.tab("three");
    //*/

    ui.update();

    let mpos = [0, 0];
    let start_mpos = [0, 0];
    let mdown = false;

    ui.addEventListener("mousedown", (e) => {
      mpos[0] = e.x;
      mpos[1] = e.y;

      start_mpos[0] = mpos[0];
      start_mpos[1] = mpos[1];

      mdown = true;
    });

    ui.addEventListener("mouseup", (e) => {
      mpos[0] = e.x;
      mpos[1] = e.y;
      mdown = false;
    });

    ui.addEventListener("mousemove", (e) => {
      mpos[0] = e.x;
      mpos[1] = e.y;

      if (!mdown) 
        return;

      if (e.button == 0) {
        let dx = start_mpos[0] - mpos[0];
        let dy = start_mpos[1] - mpos[1];

        let dis = Math.sqrt(dx*dx + dy*dy);
        if (dis > 5) {
          mdown = false;
          _appstate.screen.areaDragTool();
        }
      }

      e.preventDefault();
      e.stopPropagation();
    });

    this.shadow.appendChild(this.canvas);
  }

  static define() { return {
    tagname  : "canvasarea-x",
    areaname : "canvas_area",
    uiname   : "Canvas Area",
    areatype : ScreenArea.AreaTypes.TEST_CANVAS_EDITOR
  };}

  copy() {
    let ret = document.createElement("canvasarea-x");

    ret.ctx = this.ctx;

    return ret;
  }

  draw() {
    if (this.ctx !== undefined) {
      this.g.clearRect(0, 0, this.canvas.width, this.canvas.height);
    }
  }

  on_resize(size) {
    super.on_resize();

    this.canvas.width = size[0];
    this.canvas.height = size[1];

    if (window.redraw_all)
      window.redraw_all();
  }

  update() {
    //console.log("update");

    //this.canvas.style["left"] = this.pos[0];
    //this.canvas.style["top"] = this.pos[1];
    super.update();
    this.ui.update();
  }

  static fromSTRUCT(reader) {
    let ret = document.createElement("canvasarea-x");
    ret.ctx = _appstate.ctx;

    reader(ret);
    ret.remakeUI();
    ret.afterSTRUCT();

    return ret;
  }

}

CanvasArea.STRUCT = nstructjs.STRUCT.inherit(CanvasArea, ScreenArea.Area) + `
  }
`;

nstructjs.manager.add_class(CanvasArea);
ScreenArea.Area.register(CanvasArea);

export class AppState extends events.EventHandler {
  constructor() {
    super();

    this.lastsize = [0, 0];

    this.last_save = 0;

    this.ctx = new toolsys.Context();
    this.toolstack = new toolsys.ToolStack();

    this._modal_dom_root = undefined;

    this.initAPI();

    //this.makeGUI();
    this.image = undefined;
    this.enumval = 0;
  }

  initAPI() {
    this.api = new controller.DataAPI();
    this.api.prefix = "state.";
    let stt = this.api.mapStruct(AppState);

    let def = stt.enum("enumval",  "enumval", {A : 0, Test : 1, Triangle : 2, Bleh : 3, Yay : 4});
    def.icons({
      A : Icons.DELETE,
      Test : Icons.UNDO,
      Triangle : Icons.REDO,
      Bleh : Icons.FOLDER,
      Yay : Icons.BACKSPACE
    });

  }

  makeScreen() {
    this.screen = this.gui = document.createElement("screen-x");
    this.screen.ctx = this.ctx;
    this.screen.listen();

    this.screen.size = [window.innerWidth, window.innerHeight];

    let sarea = document.createElement("screenarea-x");

    sarea.size[0] = window.innerWidth;
    sarea.size[1] = window.innerHeight;

    this.screen.makeBorders();

    sarea.setCSS();
    this.screen.setCSS();

    document.body.appendChild(this.screen);
    this.screen.appendChild(sarea);

    sarea.switch_editor(CanvasArea);

    let canvas = sarea.area.canvas;
    this.canvas = canvas;

    canvas.width = window.innerWidth;
    canvas.height = window.innerHeight;

    //canvas.style["width"] = "100%";
    //canvas.style["height"] = "100%";
    //canvas.style["z-index"] = 2;

    //this.g = canvas.getContext("2d");

    console.log(sarea, "SAREA!");

    this.screen.makeBorders();
    this._modal_dom_root = this.screen;

    this.screen.splitArea(this.screen.sareas[0], 0.333, false)
    this.screen.splitArea(this.screen.sareas[0], 0.333, true)
  }

  makeGUI() {
    this.gui = document.createElement("container-x")
    this.gui.ctx = this.ctx;
    this.gui.style["width"] = "300px";
    this.gui.listen();

    document.body.appendChild(this.gui);
    this.gui.float(300, 10, 1);

    for (let i=0; i<5; i++) {
      this.gui.slider("EXAMPLE_PARAM", "Example", 128, 1, 512, true, false);
    }
    this.gui.button("Test", (e) => {
      console.log("click");
    });

    this.gui.check("EXAMPLE_OPTION", "ExampleCheck0");
    //*
    this.gui.listenum("EXAMPLE_ENUM", "Enum", {A : 0, Test : 1, Triangle : 2, Bleh :                  3, Yay : 4}, 0, 
    (name, id) => {
      console.log("enum change", name, id);
    }, {
      A : Icons.DELETE,
      Test : Icons.UNDO,
      Triangle : Icons.REDO,
      Bleh : Icons.FOLDER,
      Yay : Icons.BACKSPACE
    });
    //*/

    for (let i=0; i<5; i++) {
      this.gui.update();
    }

    /*this.gui.check("EXAMPLE_OPTION", "Example Option");

    this.gui.button("load_image", "Load Image", () => {
      console.log("load image!");
      image.loadImageFile().then((imagedata) => {
        console.log("got image!", imagedata);

        this.image = imagedata;
        window.redraw_all();
      });
    });
    //*/

    this.gui.load();
  }

  setsize() {
    var w = window.innerWidth, h = window.innerHeight;

    var eventfire = this.lastsize[0] != w || this.lastsize[1] != h;

    this.lastsize[0] = w;
    this.lastsize[1] = h;

    //if (this.canvas.width != w)
    //  this.canvas.width = w;
    //if (this.canvas.height != h)
    //  this.canvas.height = h;

    if (eventfire)
      this.screen.on_resize([w, h]);
  }

  draw() {
    if (this.canvas === undefined) {
      window.redraw_all();
      return;
    }

    this.setsize();

    this.screen.draw();

    //this.g.clearRect(0, 0, this.canvas.width, this.canvas.height);

    //if (this.image !== undefined) {
    //  this.g.putImageData(this.image, 20, 20);
    //}

  }

  genFile() {
    return JSON.stringify(this);
  }

  save() {
    localStorage[STARTUP_FILE_NAME] = this.genFile();
  }

  load(buf) {
    buf = buf === undefined ?  localStorage[STARTUP_FILE_NAME] : buf;

    try {
      this.loadJSON(JSON.parse(buf));
      this.gui.load();
    } catch (error) {
      util.print_stack(error);

      console.warn("Failed to load start-up file");
      return false;
    }

    this.gui.update();
    return true;
  }

  toJSON() {
    return {
      version : cconst.APP_VERSION,
      config  : config
    };
  }

  loadJSON(obj) {

    //console.log(obj.config, "yay");

    if (obj.config !== undefined) {
      config.loadJSON(obj.config);
    }

    window.redraw_all();
    return this;
  }

  on_resize(newsize) {
    console.log("resize event");

    this.screen.on_resize(newsize);
  }

  on_mousedown(e) {
  }

  on_mousemove(e) {
  }

  on_mouseup(e) {
  }

  menuTest() {
    console.log("menu test!");

    let menu = document.createElement("menu-x");

    menu.addItem("one");
    menu.addItem("two");
    menu.addItem("three");
    menu.addItem("test");
    menu.addItem("Item");
    menu.addItem("Triangle");
    menu.addItem("Vertex");

    this.screen.appendChild(menu);
    let mpos = this.screen.mpos;
    let x = mpos[0], y = mpos[1];

    menu.start();
    menu.float(x, y, 50);
    return;

    let dpi = ui_base.UIBase.getDPI();
    let rect = menu.dom.getClientRects();
    let maxy = window.innerHeight - 10;
    let maxx = window.innerWidth - 10;

    console.log(rect.length > 0 ? rect[0] : undefined)

    if (rect.length > 0) {
      rect = rect[0];
      console.log(y + rect.height);
      if (y + rect.height > maxy) {
        console.log("greater");
        y = maxy - rect.height - 1;
      }

      if (x + rect.width > maxx) {
        console.log("greater");
        x = maxx - rect.width - 1;
      }
    }

    menu.float(x, y, 50);
  }

  on_tick() {
    this.setsize();

    /*
    if (util.time_ms() - this.last_save > 900) {
      console.log("autosaving");
      this.save();

      this.last_save = util.time_ms();
    }
    //*/
  }

  on_keydown(e) {
    console.log(e.keyCode);

    switch (e.keyCode) {
      case 87: //wkey
        this.menuTest();
        break;
      case 75: //kkey
        this.screen.splitTool();
        break;
      case 67: //ckey
        this.screen.areaDragTool();
        break;
      case 90: //zkey
        if (e.ctrlKey && e.shiftKey && !e.altKey) {
          this.toolstack.redo();
          window.redraw_all();
        } else if (e.ctrlKey && !e.altKey) {
          this.toolstack.undo();
          window.redraw_all();
        }
        break;
      case 89: //ykey
        if (e.ctrlKey && !e.shiftKey && !e.altKey) {
          this.toolstack.redo();
          window.redraw_all();
        }
        break;
    }
  }
}

export function start() {
  if (window._appstate !== undefined) { 
    //already initialized
    return;
  }

  window._appstate = new AppState();
  _appstate.makeScreen();

  _appstate.pushModal(_appstate.screen, true);

  var animreq = undefined;
  function dodraw() {
    animreq = undefined;
    _appstate.draw();
  }

  window.redraw_all = function redraw_all() {
    if (animreq !== undefined) {
      return;
    }

    animreq = requestAnimationFrame(dodraw);
  }

  if (STARTUP_FILE_NAME in localStorage) {
    _appstate.load();
    window.redraw_all();
  }

  window.setInterval(function() {
    _appstate.on_tick();
  }, 250);
}

class Config {
  constructor() {
    this.EXAMPLE_PARAM = 128;
    this.EXAMPLE_OPTION = false;
    this.EXAMPLE_ENUM = 0;
  }
  
  copy() {
    let ret = JSON.parse(JSON.stringify(this));
    
    ret.copy = this.copy;
    ret.loadJSON = this.loadJSON;
    ret.toJSON = this.toJSON;
    
    return ret;
  }

  toJSON() {
    let ret = {};
    
    for (let k in this) {
      let v = this[k];
      
      if (typeof v != "function") {
        ret[k] = v;
      }
    }
    
    return ret;
  } 

  loadJSON(obj) {
    for (let k in obj) {
      let v = obj[k];
      
      if (typeof v == "function" || (typeof v == "object" && v.is_new_curve)) {
        continue;
      }
      
      if (!(k in this)) {
        console.log("unknown config key", k);
        continue;
      }
      
      this[k] = obj[k];
    }
    
    return this;
  }
}

var config = new Config();
export default config;

import * as toolprop from './toolprop.js';

let PropFlags = toolprop.PropFlags,
    PropTypes = toolprop.PropTypes;

export const DataFlags = {
  READ_ONLY : 1
};

export class DataPathError extends Error {
};

export class ToolPropertyIface {
  constructor() {
  }
  
  static tooldef() {return {
    uiname      : "!untitled tool",
    icon        : -1,
    toolpath    : "logical_module.tool", //logical_module need not match up to real module name
    description : undefined,
    hotkey      : undefined,
    is_modal    : false,
    inputs      : {}, //tool properties
    outputs     : {}  //tool properties
  }}
};

export let tool_classes = {};
let tool_idgen = 1;
Symbol.ToolID = Symbol("toolid");

function toolkey(cls) {
  if (!(Symbol.ToolID in cls)) {
    cls[Symbol.ToolID] = tool_idgen++;
  }
  
  return cls[Symbol.ToolID];
}

export class ModelInterface {
  constructor() {
    this.prefix = "";
  }
  
  getToolDef(path) {
    throw new Error("implement me");
  }
  
  createTool(path, inputs={}, constructor_argument=undefined) {
    throw new Error("implement me");
  }
  
  //returns tool class, or undefined if one cannot be found for path
  parseToolPath(path) {
    throw new Error("implement me");
  }
  
  execTool(ctx, path, inputs={}, constructor_argument=undefined) {
    return new Promise((accept, reject) => {
      let tool;
      
      try {
        tool = this.createTool(ctx, path ,inputs, constructor_argument);
      } catch (error) {
        reject(error);
        return;
      }
      
      //give client a chance to change tool instance directly
      accept(tool);
      
      //execute
      ctx.state.toolstack.execTool(tool);
    });
  }
  
  static toolRegistered(tool) {
    throw new Error("implement me");
  }
  
  static registerTool(tool) {
    throw new Error("implement me");
  }
  
  /*returns {
    obj : [object owning property key]
    parent : [parent of obj]
    key : [property key]
    value : [value of property]
    prop : [optional toolprop.ToolProperty representing the property definition]
    struct : [optional datastruct representing the type, if value is an object]
    mass_set : mass setter string, if controller implementation supports it
  }
  */
  resolvePath(ctx, path) {
    //path = prefix + path;
  }
  
  setValue(ctx, path, val) {
    let res = this.resolvePath(ctx, path);
    let prop = res.prop;

    if (prop !== undefined && (prop.flag & PropFlags.USE_CUSTOM_GETSET)) {
      prop.setValue(val);
      return;
    }

    if (prop !== undefined && (prop.type & (PropTypes.INT|PropTypes.FLOAT)) && prop.range) {
      console.log(prop.range);
      val = Math.min(Math.max(val, prop.range[0]), prop.range[1]);
    }

    let old = res.obj[res.key];

    if (res.type == "enum") {
      if (val) {
        res.obj[res.key] = res.arg;
      }
    } else if (res.type == "flag") {
      if (val) {
        res.obj[res.key] |= res.arg;
      } else {
        res.obj[res.key] &= ~res.arg;
      }
    } else {
      res.obj[res.key] = val;
    }

    if (prop !== undefined) {
      prop.dataref = res.obj;
      prop._fire("change", res.obj[res.key], old);
    }
  }
  
  getValue(ctx, path) {
    let ret = this.resolvePath(ctx, path);
    
    if (ret.prop !== undefined && (ret.prop.flag & PropFlags.USE_CUSTOM_GETSET)) {
      ret.prop.dataref = ret.obj;
      
      return ret.prop.getValue();
    }
    
    return this.resolvePath(ctx, path).value;
  }
}

export const DataTypes = {
  STRUCT : 0,
  PROP : 1
}

export class DataPath extends ModelInterface {
  constructor(path, apiname, prop, type=DataTypes.PROP) {
    super();
    this.type = type;
    this.data = prop;
    this.apiname = apiname;
    this.path = path;
    this.struct = undefined;
  }
  
  read_only() {
    this.flag |= DataFlags.READ_ONLY;
    return this;      
  }
  
  //get/set will be called 
  //like other callbacks,
  //e.g. the real object owning the property
  //will be stored in this.dataref
  customGetSet(get, set) {
    this.data.flag |= PropFlags.USE_CUSTOM_GETSET;
    
    this.data._getValue = this.data.getValue;
    this.data._setValue = this.data.setValue;
    
    if (get)
      this.data.getValue = get;
    
    if (set)
      this.data.setValue = set;
    
    return this;
  }
  
  //db will be executed with underlying data object
  //that contains this path in 'this.dataref'
  on(type, cb) {
    if (this.type == DataTypes.PROP) {
      this.data.on(type, cb);
    } else {
      throw new Error("invalid call to DataPath.on");
    }
    
    return this;
  }
  
  off(type, cb) {
    if (this.type == DataTypes.PROP) {
      this.data.off(type, cb);
    }
  }
  
  range(min, max) {
    this.data.setRange(min, max);
    return this;
  }
  
  decimalPlaces(n) {
    this.data.setDecimalPlaces(n);
    return this;
  }
  
  radix(r) {
    this.data.setRadix(r);
    return this;
  }
  
  step(s) {
    this.data.setStep(s);
    return this;
  }
  
  icon(i) {
    this.data.setIcon(i);
    return this;
  }
  
  icons(icons) { //for enum/flag properties
    this.data.addIcons(icons);
    return this;
  }
  
  description(d) {
    this.data.description = d;
    return this;
  }
}

export class DataStruct {
  constructor(members=[], name="unnamed") {
    this.members = [];
    this.name = name;
    this.pathmap = {};
    
    for (let m of members) {
      this.add(m);
    }
  }
  
  struct(name) {
    let ret = new DataStruct();
    return ret;
  }
  
  float(path, apiname, uiname, description) {
    let prop = new toolprop.FloatProperty(0, apiname, uiname, description);
    
    let dpath = new DataPath(path, apiname, prop);
    this.add(dpath);
    return dpath;
  }
  
  string(path, apiname, uiname, description) {
    let prop = new toolprop.StringProperty(undefined, apiname, uiname, description);

    let dpath = new DataPath(path, apiname, prop);
    this.add(dpath);
    return dpath;
  }
  
  int(path, apiname, uiname, description) {
    let prop = new toolprop.IntProperty(0, apiname, uiname, description);
    
    let dpath = new DataPath(path, apiname, prop);
    this.add(dpath);
    return dpath;
  }
  
  enum(path, apiname, enumdef, uiname, description) {
    let prop = new toolprop.EnumProperty(undefined, enumdef, apiname, uiname, description);
    
    let dpath = new DataPath(path, apiname, prop);
    this.add(dpath);
    return dpath;
  }
  
  array(path, apiname, struct) {
    //do nothing for now
  }
  
  flags(path, apiname, enumdef, uiname, description) {
    let prop = new toolprop.FlagProperty(undefined, enumdef, apiname, uiname, description);
    
    let dpath = new DataPath(path, apiname, prop);
    this.add(dpath);
    return dpath;
  }
  
  add(m) {
    this.members.push(m);
    m.parent = this;
    
    this.pathmap[m.path] = m;
    
    return this;
  }
}

let _map_struct_idgen = 1;
let _map_structs = {};

export class DataAPI extends ModelInterface {
  constructor() {
    super();
  }

  mapStruct(cls, auto_create=true) {
    let key = cls.__dp_map_id;
    
    if (key === undefined && auto_create) {
      key = cls.__dp_map_id = _map_struct_idgen++;
      _map_structs[key] = new DataStruct(undefined, cls.name);
    }
    
    return _map_structs[key];
  }

  resolvePath(ctx, path) {
    let splitchars = new Set([".", "[", "]", "=", "&"]);
    
    path = "." + this.prefix + path;
    //console.log(path);
    
    let p = [""];
    for (let i=0; i<path.length; i++) {
      let s = path[i];
      
      if (splitchars.has(s)) {
        if (s != "]") {
          p.push(s);
        }
        
        p.push("");
        continue;
      }
      
      p[p.length-1] += s;
    }
    
    for (let i=0; i<p.length; i++) {
      p[i] = p[i].trim();
      
      if (p[i].length == 0) {
        p.remove(p[i]);
        i--;
      }
      
      let c = parseInt(p[i]);
      if (!isNaN(c)) {
        p[i] = c;
      }
    }
    
    //console.log(p);
    let i = 0;
    
    let parent1, obj = ctx, parent2;
    let key = undefined;
    let dstruct = undefined;
    let arg = undefined;
    let type = "normal";
    
    while (i < p.length-1) {
      let a = p[i];
      let b = p[i+1];
      
      if (a == "." || a == "[") {
        key = b
        
        parent2 = parent1;
        parent1 = obj;
        obj = obj[b];
        
        if (obj === undefined || obj === null) {
          break;
        }
        
        if (typeof obj == "object") {
          dstruct = this.mapStruct(obj.constructor, false);
        }
        
        i += 2;
        continue;
      } else if (a == "&") {
        obj &= b;
        arg = b;
        
        i += 2;
        type = "flag";
        continue;
      } else if (a == "=") {
        obj = obj == b;
        arg = b;
        i += 2;
        type = "enum";
        continue;
      }
      
      i++;
    }
    /*
    console.log(p);
    console.log(parent2);
    console.log(parent1);
    console.log(obj);
    //*/
    let prop;
    
    if (dstruct !== undefined && dstruct.pathmap[key]) {
      let dpath = dstruct.pathmap[key];
      
      if (dpath.type == DataTypes.PROP) {
        prop = dpath.data;
      }
    }
    
    return {
      parent : parent2,
      obj : parent1,
      value : obj,
      key : key,
      dstruct : dstruct,
      prop : prop,
      arg : arg,
      type : type
    };
  }
  
  /*returns {
    obj : [object owning property key]
    parent : [parent of obj]
    key : [property key]
    value : [value of property]
    prop : [optional toolprop.ToolProperty representing the property definition]
    struct : [optional datastruct representing the type, if value is an object]
  }
  */    
  resolvePathold(ctx, path) {
    path = this.prefix + path;
    path = path.replace(/\[/g, ".").replace(/\]/g, "").trim().split(".");
    
    let parent1, obj = ctx, parent2;
    let key = undefined;
    let dstruct = undefined;
    
    for (let c of path) {
      let c2 = parseInt(c);
      if (!isNaN(c2)) {
        c = c2;
      }
      
      parent2 = parent1;
      parent1 = obj;
      key = c;

      if (typeof obj == "number") {
        //bitmask test
        obj = obj & c;
        break;
      }

      obj = obj[c];
      
      if (typeof obj == "object") {
        dstruct = this.mapStruct(obj.constructor, false);
      }
    }
    
    let prop;
    
    if (dstruct !== undefined && dstruct.pathmap[key]) {
      let dpath = dstruct.pathmap[key];
      
      if (dpath.type == DataTypes.PROP) {
        prop = dpath.data;
      }
    }
    
    return {
      parent : parent2,
      obj : parent1,
      value : obj,
      key : key,
      dstruct : dstruct,
      prop : prop
    };
  }
  
  getToolDef(path) {
    let cls = this.parseToolPath(path);
    if (cls === undefined) {
      throw new DataPathError("unknown path \"" + path + "\"");
    }
    
    return cls.tooldef();
  }
  
  parseToolPath(path) {
    //path = path.trim().split(" ");
    
    for (let key in tool_classes) {
      let cls = tool_classes[key];
      
      let def = cls.tooldef();
      if (def.toolpath == path) {
        return cls;
      }
    }
    
    return undefined;
  }
  
  createTool(ctx, path, inputs={}, constructor_argument=undefined) {
    let cls = this.parseToolPath(path);
    
    if (cls === undefined) {
      throw new DataPathError("unknown path \"" + path + "\"");
    }
    
    let tool = new cls(constructor_argument);
    if (inputs !== undefined) {
      for (let k in inputs) {
        if (!(k in tool.inputs)) {
          console.warn(cls.tooldef().uiname + ": Unknown tool property \"" + k + "\"");
          continue;
        }
        
        tool.inputs[k].setValue(inputs[k]);
      }
    }
    
    return tool;
  }
  
  static toolRegistered(cls) {
    let key = toolkey(cls);
    
    return key in tool_classes;
  }
  
  static registerTool(cls) {
    let key = toolkey(cls);
    
    if (!(key in tool_classes)) {
      tool_classes[key] = cls;
    }
  }
}

export function registerTool(cls) {
  return DataAPI.registerTool(cls);
