export function expand_line(l, margin) {
  var c=new Vector3();
  c.add(l[0]);
  c.add(l[1]);
  c.mulScalar(0.5);
  l[0].sub(c);
  l[1].sub(c);
  var l1=l[0].vectorLength();
  var l2=l[1].vectorLength();
  l[0].normalize();
  l[1].normalize();
  l[0].mulScalar(margin+l1);
  l[1].mulScalar(margin+l2);
  l[0].add(c);
  l[1].add(c);
  return l;
};

export function colinear(a, b, c) {
