          console.log("Error: missing screen ref");
          return;
        }

        mdown = false;
        console.log("area drag tool!");
        screen.areaDragTool(this.owning_sarea);
      }
    };

    row.addEventListener("mousemove", (e) => {
      return do_mousemove(e, e.pageX, e.pageY);
    }, false);

    row.addEventListener("mouseup", (e) => {
      if (!mpre(e)) return;

