    if (this.size !== undefined) {
      this.style["position"] = "absolute";
      //this.style["left"] = this.pos[0] + "px";
      //this.style["top"] = this.pos[1] + "px";
      this.style["width"] = ~~this.size[0] + "px";
      this.style["height"] = ~~this.size[1] + "px";
