        min[i] = Math.min(min[i], b.v1[i]);
        min[i] = Math.min(min[i], b.v2[i]);
        
        max[i] = Math.max(max[i], b.v1[i]);
        max[i] = Math.max(max[i], b.v2[i]);
      }
    }
    
    this.pos[0] = min[0];
    this.pos[1] = min[1];
    
    this.size[0] = max[0] - min[0];
    this.size[1] = max[1] - min[1];
    
    this.setCSS();
