let UIBase = ui_base.UIBase, 
    PackFlags = ui_base.PackFlags,
    IconSheets = ui_base.IconSheets;

export let tab_idgen = 1;
let debug = false;

function getpx(css) {
  return parseFloat(css.trim().replace("px", ""))
}

export class TabItem {
  constructor(name, id, tooltip="") {
    this.name = name;
    this.id = id;
    this.tooltip = tooltip;
    
    this.size = [0, 0];
    this.pos = [0, 0];
  }
}

export class ModalTabMove extends events.EventHandler {
  constructor(tab, tbar, dom) {
    super();
    
    this.dom = dom;
    this.tab = tab;
    this.tbar = tbar;
    
    this.mpos = undefined;
  }
  
  finish() {
    if (debug) if (debug) console.log("finish");
    
    this.tbar.tool = undefined;
    this.popModal(this.dom);
    this.tbar.update(true);
  }
  
  on_mousedown(e) {
    if (debug) console.log("yay");
    
    this.finish();
  }
  
  on_touchstart(e) {
    this.finish();
  }
  
  on_touchend(e) {
    this.finish();
  }
  
  on_mouseup(e) {
    this.finish();
  }
  
  on_mousemove(e) {
    if (debug) console.log("SFSDFSD");
    return this._on_move(e, e.x, e.y);
  }
  
  on_touchmove(e) {
    if (e.touches.length == 0)
      return;
    
    let x = e.touches[0].pageX;
    let y = e.touches[0].pageY;
    
    return this._on_move(e, x, y);
  }
  
  _on_move(e, x, y) {
    let r = this.tbar.getClientRects()[0];
    let dpi = UIBase.getDPI();

    x -= r.x;
    y -= r.y;
    
    let dx, dy;
    
    x *= dpi;
    y *= dpi;
    
    if (this.mpos === undefined) {
      this.mpos = [0, 0];
      dx = dy = 0;
    } else {
      dx = x - this.mpos[0];
      dy = y - this.mpos[1];
    }
    
    if (debug) console.log(x, y, dx, dy);
    
    let tab = this.tab, tbar = this.tbar;
    let axis = tbar.horiz ? 0 : 1;
    
    if (tbar.horiz) {
      tab.pos[0] += dx;
    } else {
      tab.pos[1] += dy;
    }
    
    let ti = tbar.tabs.indexOf(tab);
    let next = ti < tbar.tabs.length-1 ? tbar.tabs[ti+1] : undefined;
    let prev = ti > 0 ? tbar.tabs[ti-1] : undefined;
    
    if (next !== undefined && tab.pos[axis] > next.pos[axis]) {
      tbar.swapTabs(tab, next);
    } else if (prev !== undefined && tab.pos[axis] < prev.pos[axis]+prev.size[axis]*0.5) {
      tbar.swapTabs(tab, prev);
    }
    
    tbar.update(true);
    
    this.mpos[0] = x;
    this.mpos[1] = y;
  }
  
  on_keydown(e) {
    if (debug) console.log(e.keyCode);
    
    switch (e.keyCode) {
      case 27: //escape
      case 32: //space
      case 13: //enter
      case 9: //tab
        this.finish();
        break;
    }
  }
}

export class TabBar extends UIBase {
  constructor() {
    super();
    
    let style = document.createElement("style");
    let canvas = document.createElement("canvas");
    
    this.tabs = [];
    this.tabs.active = undefined;
    this.tabs.highlight = undefined;

    this._last_bgcolor = undefined;

    canvas.style["width"] = "145px";
    canvas.style["height"] = "45px";
    
    this.r = 5;
    
    this.canvas = canvas;
    this.g = canvas.getContext("2d");
    
    style.textContent = `
    `;
    
    this.shadow.appendChild(style);
    this.shadow.appendChild(canvas);
    
    this._last_dpi = undefined;
    this._last_pos = undefined;
    
    this.horiz = true;
    this.onchange = undefined;
    
    let mx, my;
    
    let do_element = (e) => {
      for (let tab of this.tabs) {
        let ok;

        if (this.horiz) {
          ok = mx >= tab.pos[0] && mx <= tab.pos[0] + tab.size[0];
        } else {
          ok = my >= tab.pos[1] && my <= tab.pos[1] + tab.size[1];
        }
        
        if (ok && this.tabs.highlight !== tab) {
          this.tabs.highlight = tab;
          this.update(true);
        }
      }
    }
    
    let do_mouse = (e) => {
      let r = this.canvas.getClientRects()[0];
      
      mx = e.x - r.x;
      my = e.y - r.y;
      
      let dpi = ui_base.UIBase.getDPI();
      
      mx *= dpi;
      my *= dpi;
      
      do_element(e);
    }
    
    let do_touch = (e) => {
      let r = this.canvas.getClientRects()[0];
      
      if (debug) console.log(e.touches);
      
      mx = e.touches[0].pageX - r.x;
      my = e.touches[0].pageY - r.y;
      
      let dpi = ui_base.UIBase.getDPI();
      
      mx *= dpi;
      my *= dpi;
      
      do_element(e);
    }
    
    this.canvas.addEventListener("mousemove", (e) => {
      if (debug) console.log("yay");
      
      let r = this.canvas.getClientRects()[0];
      do_mouse(e);
      
      e.preventDefault();
      e.stopPropagation();
    }, false);
    
    this.canvas.addEventListener("touchstart", (e) => {
      if (e.touches.length == 0) {
        return;
      }
      
      do_touch(e);
      
      let ht = this.tabs.highlight;
      
      if (ht !== undefined && this.tool === undefined) {
        this.setActive(ht);

        let edom = this.getScreen();
        let tool = new ModalTabMove(ht, this, edom);
        this.tool = tool;
        
        tool.pushModal(edom, false);
      }
      
      e.preventDefault();
      e.stopPropagation();
    }, false);
    
    this.canvas.addEventListener("mousedown", (e) => {
      do_mouse(e);

      if (debug) console.log("mdown");

      if (e.button !== 0) {
        return;
      }
      
      let ht = this.tabs.highlight;
      
      if (ht !== undefined && this.tool === undefined) {
        this.setActive(ht);
        
        let edom = this.getScreen();
        let tool = new ModalTabMove(ht, this, edom);
        this.tool = tool;
        
        tool.pushModal(edom, false);
      }
      
      e.preventDefault();
      e.stopPropagation();
    }, false);
  }
  
  getTab(name) {
    for (let tab of this.tabs) {
      if (tab.name === name)
        return tab;
    }
    
    return undefined;
  }
  
  saveData() {
    let taborder = [];
    
    for (let tab of this.tabs) {
      taborder.push(tab.name);
    }
    
    let act = this.tabs.active !== undefined ? this.tabs.active.name : "null";
    
    return {
      taborder : taborder,
      active : act
    };
  }
  
  loadData(obj) {
    if (!obj.taborder) {
      return;
    }
    
    let tabs = this.tabs;
    let ntabs = [];
    
    ntabs.active = undefined;
    ntabs.highlight = undefined;
    
    for (let tname of obj.taborder) {
      let tab = this.getTab(tname);
      
      if (tab === undefined) {
        continue;
      }
      
      if (tab.name === obj.active) {
        ntabs.active = tab;
      }
      
      ntabs.push(tab);
    }
    
    for (let tab of tabs) {
      if (ntabs.indexOf(tab) < 0) {
        ntabs.push(tab);
      }
    }
    
    this.tabs = ntabs;
    this.update(true);
    
    return this;
  }
  
  static setDefault(e) {
    e.setAttribute("bar_pos", "top");
    e.updatePos(true);
    
    return e;
  }
  
  swapTabs(a, b) {
    let tabs = this.tabs;
    
    let ai = tabs.indexOf(a);
    let bi = tabs.indexOf(b);
    
    tabs[ai] = b;
    tabs[bi] = a;
    
    this.update(true);
  }
  
  addTab(name, id, tooltip="") {
    this.tabs.push(new TabItem(name, id, tooltip));
    this.update(true);
    
    if (this.tabs.length == 1) {
      this.setActive(this.tabs[0]);
    }
  }
  
  updatePos(force_update=false) {
    let pos = this.getAttribute("bar_pos");
    
    if (pos != this._last_pos || force_update) {
      this._last_pos = pos;
      
      this.horiz = pos == "top" || pos == "bottom";
      if (debug) console.log("tab bar position update", this.horiz);

      if (this.horiz) {
        this.style["width"] = "100%";
        delete this.style["height"];
      } else {
        this.style["height"] = "100%";
        delete this.style["width"];
      }
      
      this._redraw();
    }
  }
  
  updateDPI(force_update=false) {
    let dpi = UIBase.getDPI();
    
    if (dpi !== this._last_dpi) {
      if (debug) console.log("DPI update!");
      this._last_dpi = dpi;
      
      this.updateCanvas(true);
    }
  }
  
  updateCanvas(force_update=false) {
    let canvas = this.canvas;
     
    let dpi = UIBase.getDPI();
    
    let rwidth = getpx(this.canvas.style["width"]);
    let rheight = getpx(this.canvas.style["height"]);
    
    let width = Math.ceil(rwidth*dpi);
    let height = Math.ceil(rheight*dpi);
    
    let update = force_update;
    if (this.horiz) {
      update = update || canvas.width != width;
    } else {
      update = update || canvas.height != height;
    }
    
    if (update) {
      canvas.width = width;
      canvas.height = height;
      
      this._redraw();
    }
  }
  
  _layout() {
    let g = this.g;
    
    if (debug) console.log("tab layout");
    
    let dpi = UIBase.getDPI();
    let tsize = this.getDefault("TabTextSize")*1;
    
    g.font = ui_base._getFont(tsize, "TabText");
    
    let axis = this.horiz ? 0 : 1;
    
    let pad = 4*dpi + Math.ceil(tsize*dpi*0.25);
    let x = pad;
    let y = 0;
    
    let h = tsize*dpi + Math.ceil(tsize*dpi*0.5);
    
    for (let tab of this.tabs) {
      let w = g.measureText(tab.name).width//*dpi;
      
      //don't interfere with tab dragging
      let bad = this.tool !== undefined && tab === this.tabs.active;
      
      if (!bad) {
        tab.pos[axis] = x;
        tab.pos[axis^1] = y;
      }
      
      //tab.size = [0, 0];
      tab.size[axis] = w+pad*2;
      tab.size[axis^1] = h;
      
      x += w + pad*2;
    }
    
    if (this.horiz) {
      this.canvas.style["width"] = Math.ceil(x/dpi + pad/dpi) + "px";
      this.canvas.style["height"] = Math.ceil(h/dpi) + "px";
    } else {
      this.canvas.style["height"] = Math.ceil(x/dpi + pad/dpi) + "px";
      this.canvas.style["width"] = Math.ceil(h/dpi) + "px";
    }
    
    //this.canvas.width = x;
  }
  
  setActive(tab) {
    let update = tab !== this.tabs.active;
    this.tabs.active = tab;
    
    if (update) {
      if (this.onchange)
        this.onchange(tab);
      
      this.update(true);
    }
  }
  
  _redraw() {
    let g = this.g;
    let bgcolor = this.getDefault("DefaultPanelBG");
    
    if (debug) console.log("tab draw");
    
    g.clearRect(0, 0, this.canvas.width, this.canvas.height);
    
    g.beginPath();
    g.rect(0, 0, this.canvas.width, this.canvas.height);
    g.fillStyle = bgcolor;
    g.fill();
    
    let dpi = UIBase.getDPI();
    let tsize = this.getDefault("TabTextSize");
    
    g.font = ui_base._getFont(tsize, "TabText");
    tsize *= dpi;
    
    g.lineWidth = 2;
    g.strokeStyle = this.getDefault("TabStrokeStyle1");
      
    let r = this.r;
    this._layout();
    
    for (let tab of this.tabs) {
      if (tab === this.tabs.active)
        continue;

      let x = tab.pos[0], y = tab.pos[1];
      let w = tab.size[0], h = tab.size[1];
      let tw = g.measureText(tab.name).width;
      
      let x2 = x + (tab.size[this.horiz^1]-tw)*0.5;
      let y2 = y + tsize*1.0;
      
      g.beginPath();
      g.rect(x, y, w, h);
      g.fillStyle = this.getDefault("TabInactive");
      g.fill();
        
      if (tab === this.tabs.highlight) {
        let p = 2;
        
        g.beginPath();
        g.rect(x+p, y+p, w-p*2, h-p*2);
        g.fillStyle = this.getDefault("TabHighlight");
        g.fill();
      }
        
      g.fillStyle = this.getDefault("TabTextColor");
      
      if (!this.horiz) {
        let x3 = 0, y3 = y2;
        
        g.save();
        g.translate(x3, y3);
        g.rotate(Math.PI/2);
        g.translate(x3-tsize, -y3-tsize*0.5);
      }
      
      g.fillText(tab.name, x2, y2);
      
      if (!this.horiz) {
        g.restore();
      }
      
      if (tab !== this.tabs[this.tabs.length-1]) {
        g.beginPath();
        g.moveTo(x+w, h-5);
        g.lineTo(x+w, 5);
        g.strokeStyle = this.getDefault("TabStrokeStyle2");
        g.stroke();
      }
    }
    
    let th = tsize;
    
    //draw active tab
    let tab = this.tabs.active;
    if (tab === undefined) {
      return;
    } else {
      let x = tab.pos[0], y = tab.pos[1];
      let w = tab.size[0], h = tab.size[1];
      let tw = g.measureText(tab.name).width;
      
      let x2 = x + (tab.size[this.horiz^1]-tw)*0.5;
      let y2 = y + tsize*1.0;
      
      g.beginPath();
      g.rect(x, y, w, h);
      g.fillStyle = bgcolor;
      g.fill();
      
      if (!this.horiz) {
        let x3 = 0, y3 = y2;
        
        g.save();
        g.translate(x3, y3);
        g.rotate(Math.PI/2);
        g.translate(-x3-tsize, -y3-tsize*0.5);
      }
      
      g.fillStyle = this.getDefault("TabTextColor");
      g.fillText(tab.name, x2, y2);

      if (!this.horiz) {
        g.restore();
      }
      
      if (tab === this.tabs.active) {
        /*
        let x = !this.horiz ? tab.y : tab.x;
        let y = !this.horiz ? tab.x : tab.y;
        let w = !this.horiz ? tab.size[1] : tab.size[0];
        let h = !this.horiz ? tab.size[0] : tab.size[1];
        
        if (!this.horiz) {
          //g.save();
          //g.translate(0, y);
          //g.rotate(Math.PI/16);
          //g.translate(0, -y);
        }//*/
        
        g.beginPath();
        if (this.horiz) {
          g.moveTo(0, h);
          g.lineTo(x-r, h);
          g.lineTo(x, h-r);
          g.lineTo(x, r);
          g.lineTo(x+r, 0);
          g.lineTo(x+w-r, 0);
          g.lineTo(x+w, r);
          g.lineTo(x+w, h-r);
          g.lineTo(x+w+r, h);
          g.lineTo(this.canvas.width, h);
        } else {
          g.moveTo(w, 0);
          g.lineTo(w, y-r);
          g.lineTo(w-r, y);
          g.lineTo(r, y);
          g.lineTo(0, y+r);
          g.lineTo(0, y+h-r);
          g.lineTo(r, y+h);
          g.lineTo(w-r, y+h);
          g.lineTo(w, y+h+r);
          
          g.lineTo(w, this.canvas.height);
        }
        
        let cw = this.horiz ? this.canvas.width : this.canvas.height;
        
        g.stroke();
        

        if (!this.horiz) {
          //g.restore();
        }
      }
    }
  }

  updateStyle() {
    if (this._last_bgcolor != this.getDefault("DefaultPanelBG")) {
      this._last_bgcolor = this.getDefault("DefaultPanelBG");

      this._redraw();
    }
  }

  update(force_update=false) {
    super.update();

    this.updateStyle();
    this.updatePos(force_update);
    this.updateDPI(force_update);
    this.updateCanvas(force_update);
  }
  
  static define() {return {
    tagname : "tabbar-x"
  };}
}
UIBase.register(TabBar);

export class TabContainer extends UIBase {
  constructor() {
    super();
    
    this.dom = document.createElement("ul");
    this.dom.setAttribute("class", `_tab_ul_${this._id}`);
    
    this.tbar = document.createElement("tabbar-x");
    this.tbar.setAttribute("class", "_tbar_" + this._id);
    this.tbar.constructor.setDefault(this.tbar);
    
    this._remakeStyle();
    this.shadow.appendChild(this.dom);
    
    let li = document.createElement("li");
    li.setAttribute("class", `_tab_li_${this._id}`);
    li.appendChild(this.tbar);
    
    let ll = li;
    
    this.dom.appendChild(li);
    
    this.tabs = {};
    
    this._last_horiz = undefined;
    this._last_bar_pos = undefined;
    this._tab = undefined;
    
    this.tbar.onchange = (tab) => {
      if (this._tab) {
        this._tab.remove();
      }
      
      this._tab = this.tabs[tab.id];
      //this._tab = document.createElement("div");
      //this._tab.innerText = "SDfdsfsdyay";
      
      let li = document.createElement("li");
      li.style["background-color"] = this.getDefault("DefaultPanelBG");
      li.setAttribute("class", `_tab_li_${this._id}`);
      li.appendChild(this._tab);
      
      //XXX why is this necassary?
      //this._tab.style["margin-left"] = "40px";
      this.dom.appendChild(li);
    }
  }
  
  static setDefault(e) {
    e.setAttribute("bar_pos", "top");
    
    return e;
  }
  
  _remakeStyle() {
    let horiz = this.tbar.horiz;
    let display = !horiz ? "inline-block" : "block";
    
    //display = "inline" //XXX
    let style = document.createElement("style");
    style.textContent = `
      ._tab_ul_${this._id} {
        list-style-type : none;
        display : ${display};
        margin : 0px;
        padding : 0px;
        ${!horiz ? "vertical-align : top;" : ""}
      }
      
      ._tab_li_${this._id} {
        display : ${display};
        margin : 0px;
        padding : 0px;
        align-self : flex-start;
        ${!horiz ? "vertical-align : top;" : ""}
      }
      
      ._tbar_${this._id} {
        list-style-type : none;
        align-self : flex-start;
        ${!horiz ? "vertical-align : top;" : ""}
      }
    `;
    
    if (this._style)
      this._style.remove();
    this._style = style;
    
    this.shadow.prepend(style);
  }
  
  tab(name, id=undefined, tooltip=undefined) {
    if (id === undefined) {
      id = tab_idgen++;
    }
    
    let col = document.createElement("colframe-x");

    this.tabs[id] = col;
    col.ctx = this.ctx;

    this.tbar.addTab(name, id, tooltip);
    //let cls = this.tbar.horiz ? ui.ColumnFrame : ui.RowFrame;

    col.parentWidget = this;
    col.setCSS();

    return col;
  }
  
  updateBarPos() {
    let barpos = this.getAttribute("bar_pos");
    
    if (barpos !== this._last_bar_pos) {
      console.log("position update!", barpos);
      
      this.horiz = barpos == "top" || barpos == "bottom";
      this._last_bar_pos = barpos;
      
      this.tbar.setAttribute("bar_pos", barpos);
      this.tbar.update(true);
      this.update();
    }
  }
  
  updateHoriz() {
    let horiz = this.tbar.horiz;
    
    if (this._last_horiz !== horiz) {
      this._last_horiz = horiz;
      this._remakeStyle();
    }
  }
  
  update() {
    super.update();
    
    if (this._tab !== undefined) {
      this._tab.update();
    }
    
    this.updateHoriz();
    this.updateBarPos();
    this.tbar.update();
  }
  
  static define() {return {
    tagname : "tabcontainer-x"
  };}
}

UIBase.register(TabContainer);

"use strict";

import * as util from './util.js';
import * as vectormath from './vectormath.js';
import * as ui_base from './ui_base.js';
import * as events from './events.js';
import * as simple_toolsys from './simple_toolsys.js';
import * as toolprop from './toolprop.js';

let EnumProperty = toolprop.EnumProperty,
    PropTypes = toolprop.PropTypes;

let UIBase = ui_base.UIBase, 
    PackFlags = ui_base.PackFlags,
    IconSheets = ui_base.IconSheets;

function getpx(css) {
  return parseFloat(css.trim().replace("px", ""))
}

//use .setAttribute("linear") to disable nonlinear sliding
export class Button extends UIBase {
  constructor() {
    super();
      
    this.boxpad = this.getDefault("BoxMargin", 4);
    let dpi = UIBase.getDPI();

    this.r = this.getDefault("BoxRadius");
    this._name = "";
    this._namePad = undefined;
    
    this._last_dpi = dpi;
    
    this._lastw = undefined;
    this._lasth = undefined;
    
    this.dom = document.createElement("canvas");
    this.g = this.dom.getContext("2d");
    
    this.dom.setAttribute("class", "canvas1");
    this.dom.tabIndex = 0;

    this._last_bg = undefined;

    this.addEventListener("keydown", (e) => {
      if (this.disabled) return;

      console.log(e.keyCode);
      
      switch (e.keyCode) {
        case 32: //spacebar
        case 13: //enter
          this.click();
          break;
      }
      
      e.preventDefault();
      e.stopPropagation();
    });
    
    this.addEventListener("focusin", () => {
      if (this.disabled) return;

      this._focus = 1;
      this._redraw();
      this.focus();
      console.log("focus2");
    });
    
    this.addEventListener("blur", () => {
      if (this.disabled) return;

      this._focus = 0;
      this._redraw();
      console.log("blur2");
    });

    this._last_disabled = false;

    //set default dimensions
    let width = ~~(this.getDefault("defaultWidth"));
    let height = ~~(this.getDefault("defaultHeight"));
    
    this.dom.style["width"] = width + "px";
    this.dom.style["height"] = height + "px";
    this.dom.style["padding"] = this.dom.style["margin"] = "0px";

    this.dom.width = Math.ceil(width*dpi); //getpx(this.dom.style["width"])*dpi;
    this.dom.height = Math.ceil(getpx(this.dom.style["height"])*dpi);
    
    let style = document.createElement("style");
    style.textContent = `.canvas1 {
      -moz-user-focus: normal;
      moz-user-focus: normal;
      user-focus: normal;
      padding : 0px;
      margin : 0px;
    }
    `;
    
    this.shadow.appendChild(style);
    let form = document.createElement("div");
    form.style["tabindex"] = 4;
    form.setAttribute("type", "hidden");
    form.type ="hidden";
    form.style["-moz-user-focus"] = "normal";
    form.setAttribute("class", "canvas1");
    form.appendChild(this.dom);

    form.style["padding"] = form.style["margin"] = "0px";

    this.shadow.appendChild(form);
    
    this.bindEvents();
    this._redraw();
  }
  
  setAttribute(key, val) {
    super.setAttribute(key, val);
    
    if (key == "name") {
      this.updateName();
      this.updateWidth();
    }
  }
  
  bindEvents() {
    let background = this.dom._background;
    let press = (e) => {
        if (this.disabled) return;

        background = this.dom._background;
        this.dom._background = this.getDefault("BoxDepressed");
        this._redraw();
        
        if (this._onpress) {
          this._onpress(this);
        }
        
        e.preventDefault();
        e.stopPropagation();
    }
    
    let depress = (e) => {
        if (this.disabled) return;

        this.dom._background = background;
        this._redraw();
        
        e.preventDefault();
        e.stopPropagation();
        
        if (e.type == "mouseup" && this.button != 0) {
          return;
        }
        
        if (this.onclick) {
          this.onclick(this);
        }
    }
    
    this.addEventListener("mousedown", press, {captured : true, passive : false});
    this.addEventListener("touchstart", press, {captured : true, passive : false});
    
    this.addEventListener("touchend", depress);
    this.addEventListener("mouseup", depress);
    
    let holdbg = this.dom._background;
    
    this.addEventListener("mouseover", (e) => {
      if (this.disabled)
        return;
      
      holdbg = this.dom._background;
      
      this.dom._background = this.getDefault("BoxHighlight");
      this._highlight = true;
      this._repos_canvas();
      this._redraw();
    })
    
    this.addEventListener("mouseout", (e) => {
      if (this.disabled)
        return;
      
      this.dom._background = holdbg; //ui_base.getDefault("BoxBG");
      this._highlight = false;
      this._repos_canvas();
      this._redraw();
    })
  }

  updateDisabled() {
    if (this._last_disabled != this.disabled) {
      this._last_disabled = this.disabled;

      //setTimeout(() => {
        this.dom._background = this.getDefault("BoxBG");

        this._repos_canvas();
        this._redraw();
        console.log("disabled update!", this.disabled, this.style["background-color"]);
      //}, 100);
    }
  }

  update() {
    super.update();

    if (this.description !== undefined && this.title != this.description) {
      this.title = this.description;
    }

    this.updateWidth();
    this.updateDPI();
    this.updateName();
    this.updateDisabled();

    if (this.background !== this._last_bg) {
      this._last_bg = this.background;
      this._repos_canvas();
      this._redraw();
    }
  }
  
  updateName() {
    let name = this.getAttribute("name");
    
    if (name !== this._name) {
      this._name = name;
      let dpi = UIBase.getDPI();
      
      let ts = this.getDefault("DefaultTextSize");
      let tw = ui_base.measureText(this._genLabel(), this.dom, this.g).width/dpi + ts*2;
      
      if (this._namePad !== undefined) {
        tw += this._namePad;
      }
      
      let w = this.getDefault("numslider_width");
      
      w = Math.max(w, tw);
      this.dom.style["width"] = w+"px";
      
      this._repos_canvas();
      this._redraw();
    }
  }
  
  updateWidth(w_add=0) {
    let dpi = UIBase.getDPI();
    
    let pack = this.packflag;
    
    if (this.parentNode !== undefined && (this.packflag & PackFlags.INHERIT_WIDTH)) {
      let p = this.parentNode;
      
      if (this._lastw != p.clientWidth + w_add) {
        console.log(p.clientWidth+w_add);
        
        this.dom.style["width"] = Math.ceil(p.clientWidth+w_add) + "px";
        this._lastw = p.clientWidth + w_add;
        
        this._repos_canvas();
      }
    }
  }
  
  _repos_canvas() {
    let dpi = UIBase.getDPI();
    
    this.dom.width = Math.ceil(getpx(this.dom.style["width"])*dpi);
    this.dom.height = Math.ceil(getpx(this.dom.style["height"])*dpi);
  }
  
  updateDPI() {
    let dpi = UIBase.getDPI();
    
    if (this._last_dpi != dpi) {
      console.log("update dpi", dpi);
      
      this._last_dpi = dpi;
      
      this.g.font = undefined; //reset font

      this._repos_canvas();        
      this._redraw();
    }
    
    if (this.style["background-color"]) {
      this.dom._background = this.style["background-color"];
      this.style["background-color"] = "";
    }
    
    //console.log(">", this.dom.style["background-color"], "-----");
    //console.log("width:", this.clientWidth)
  }
  
  _genLabel() {
    let text = "" + this._name;
    
    return text;
  }
  
  _redraw(draw_text=true) {
    //console.log("button draw");
    
    let dpi = UIBase.getDPI();
    
    ui_base.drawRoundBox(this, this.dom, this.g, undefined, undefined, this.r, undefined, undefined, this.boxpad);
    
    if (this._focus) {
      let w = this.dom.width, h = this.dom.height;
      let p = 1/dpi;
      
      //XXX remove this.g.translate lines after refactoring drawRoundBox, see comment in ui_base.js
      this.g.translate(p, p);
      let lw = this.g.lineWidth;
      this.g.lineWidth = 2*dpi;
      ui_base.drawRoundBox(this, this.dom, this.g, w-p*2, h-p*2, this.r, "stroke", this.getDefault("BoxHighlight"));
      this.g.lineWidth = lw;
      this.g.translate(-p, -p);       
    }
    
    if (draw_text) {
      this._draw_text();
    }
  }
  
  _draw_text() {
    let dpi = UIBase.getDPI();
    
    let pad = this.getDefault("BoxMargin") * dpi;
    let ts = this.getDefault("DefaultTextSize");
    
    let text = this._genLabel();
    
    //console.log(text, "text", this._name);
    
    let tw = ui_base.measureText(text, this.dom, this.g).width;
    let cx = this.dom.width/2 - tw/2;
    let cy = this.dom.height;
    
    cx = ~~cx;
    
    ui_base.drawText(~~cx + 0.5, ~~(cy - ts*0.6*dpi) + 0.5, text, this.dom, this.g);
  }
  
  static define() {return {
    tagname : "button-x",
    style : "button"
  };}
}
UIBase.register(Button);

export class ValueButtonBase extends Button {
  constructor() {
    super();
  }
  
  get value() {
    return this._value;
  }
  
  set value(val) {
    this._value = val;
    
    if (this.ctx && this.hasAttribute("datapath")) {
      UIBase.setPathValue(this.ctx, this.getAttribute("datapath"), this._value);
    }
  }
  
  updateDataPath() {
    if (!this.hasAttribute("datapath")) return;
    if (this.ctx === undefined) return;
    
    let prop = UIBase.getPathMeta(this.ctx, this.getAttribute("datapath"));
    let val =  UIBase.getPathValue(this.ctx, this.getAttribute("datapath"));

    if (val === undefined) {
      this.disabled = true;
      return;
    } else {
      if (this.disabled) {
        this._redraw();
      }

      this.disabled = false;
    }

    if (val != this._value) {
      this._value = val;
      this.updateWidth();
      this._redraw();
    }
  }
  
  update() {
    this.updateDataPath();
    
    super.update();
  }
}

//use .setAttribute("linear") to disable nonlinear sliding
export class NumSlider extends ValueButtonBase {
  constructor() {
    super();
      
    this._name = "";
    this._step = 0.1;
    this._value = 0.0;

    this._redraw();
  }
  
  swapWithTextbox() {
    let tbox = document.createElement("textbox-x");
    tbox.text = this.value.toFixed(5);
    tbox.select();
    
    this.parentNode.insertBefore(tbox, this);
    //this.remove();
    this.hidden = true;
    //this.dom.hidden = true;
    
    let finish = (ok) => {
      tbox.remove();
      this.hidden = false;
      
      if (ok) {
        let val = parseFloat(tbox.text);
        
        if (isNaN(val)) {
          console.log("EEK!");
          this.flash(ui_base.ErrorColors.ERROR);
        } else {
          this.setValue(val);
          
          if (this.onchange) {
            this.onchange(this);
          }
        }
      }
    }
    
    tbox.addEventListener("keydown", (e) => {
      console.log(e.keyCode);
      switch (e.keyCode) {
        case 27: //escape
          finish(false);
          break;
        case 13: //enter
          finish(true);
          break;
      }
    });
    
    tbox.focus();
    
    //this.shadow.appendChild(tbox);
    return;
  }
  
  bindEvents() {
    let onmousedown = (e) => {
      if (this.disabled) {
        e.preventDefault();
        e.stopPropagation();
        
        return;
      }
      
      if (e.button == 0 && e.shiftKey) {
        e.preventDefault();
        e.stopPropagation();
        
        this.swapWithTextbox();
      } else if (e.button == 0) {
        this.dragStart(e);
        
        e.preventDefault();
        e.stopPropagation();
      }
    }
    
    this.addEventListener("dblclick", (e) => {
      if (this.disabled) {
        e.preventDefault();
        e.stopPropagation();
        
        return;
      }
      
      e.preventDefault();
      e.stopPropagation();
      this.swapWithTextbox();
    });
    
    this.addEventListener("mousedown", (e) => {
      if (this.disabled) return;
      onmousedown(e);
    });
    
    this.addEventListener("touchstart", (e) => {
      if (this.disabled) return;
      console.log(e)
      
      e.x = e.touches[0].screenX;
      e.y = e.touches[0].screenY;
      
      this.dragStart(e);
      
      e.preventDefault();
      e.stopPropagation();
    }, {passive : false});
    
    //this.addEventListener("touchstart", (e) => {
    //  console.log(e);
    //});
    
    this.addEventListener("mouseover", (e) => {
      if (this.disabled) return;
      
      this.dom._background = this.getDefault("BoxHighlight");
      this._repos_canvas();
      this._redraw();
      //console.log("mouse enter");
    })
    
    this.addEventListener("mouseout", (e) => {
      if (this.disabled) return;

      this.dom._background = this.getDefault("BoxBG");
      this._repos_canvas();
      this._redraw();
      //console.log("mouse leave!");
    })
  }
  
  doRange() {
    if (this.hasAttribute("min")) {
      let min = this.getAttribute("min");
      this.value = Math.max(this.value, min);
    }
    
    if (this.hasAttribute("max")) {
      let max = this.getAttribute("max");
      this.value = Math.min(this.value, max);
    }
  }
  
  setValue(value, fire_onchange=true) {
    this.value = value;
    this.doRange();

    if (this.hasAttribute("is_int"))
      this.value = Math.floor(this.value);

    if (fire_onchange && this.onchange !== undefined) {
      this.onchange(this.value);
    }
    
    this._redraw();
  }
  
  dragStart(e) {
    if (this.disabled) return;

    let last_background = this.dom._background;
    let cancel;
    
    let startvalue = this.value;
    let value = startvalue;
    
    let startx = e.x, starty = e.y;
    
    this.dom._background = this.getDefault("BoxDepressed");
    let fire = () => {
      if (this.onchange) {
        this.onchange(this);
      }
    }


    let handlers = {
      on_keydown: (e) => {
        switch (e.keyCode) {
          case 27: //escape key
            cancel(true);
          case 13: //enter key
            cancel(false);
            break;
        }

        e.preventDefault();
        e.stopPropagation();
      },

      on_mousemove: (e) => {
        if (this.disabled) return;

        e.preventDefault();
        e.stopPropagation();

        let dx = e.x - startx;
        startx = e.x;

        if (e.shiftKey) {
          dx *= 0.1;
        }

        value += dx * this._step * 0.1;

        let dvalue = value - startvalue;
        let dsign = Math.sign(dvalue);

        if (!this.hasAttribute("linear")) {
          dvalue = Math.pow(Math.abs(dvalue), 1.333) * dsign;
        }

        this.value = startvalue + dvalue;
        this.doRange();

        /*
        if (e.shiftKey) {
          dx *= 0.1;
          this.value = startvalue2 + dx*0.1*this._step;
          startvalue3 = this.value;
        } else {
          startvalue2 = this.value;
          this.value = startvalue3 + dx*0.1*this._step;
        }*/

        this.updateWidth();
        this._redraw();
        fire();
      },

      on_mouseup: (e) => {
        cancel(false);
        e.preventDefault();
        e.stopPropagation();
      },

      on_mouseout: (e) => {
        console.log("leave");
        last_background = this.getDefault("BoxBG");

        e.preventDefault();
        e.stopPropagation();
      },

      on_mouseover: (e) => {
        console.log("over");
        last_background = this.getDefault("BoxHighlight");

        e.preventDefault();
        e.stopPropagation();
      },

      on_mousedown : (e) => {
        this.popModal();
      },
    };
    
    //events.pushModal(this.getScreen(), handlers);
    this.pushModal(handlers);

    cancel = (restore_value) => {
      if (restore_value) {
        this.value = startvalue;
        this.updateWidth();
        fire();
      }
      
      this.dom._background = last_background; //ui_base.getDefault("BoxBG");
      this._redraw();
      
      console.trace("end");
      
      this.popModal();
    }
    
    /*
    cancel = (restore_value) => {
      if (restore_value) {
        this.value = startvalue;
        this.updateWidth();
        fire();
      }
      
      this.dom._background = last_background; //ui_base.getDefault("BoxBG");
      this._redraw();
      
      console.trace("end");
      
      window.removeEventListener("keydown", keydown, true);
      window.removeEventListener("mousemove", mousemove, {captured : true, passive : false});
      
      window.removeEventListener("touchend", touchend, true);
      window.removeEventListener("touchmove", touchmove, {captured : true, passive : false});
      window.removeEventListener("touchcancel", touchcancel, true);
      window.removeEventListener("mouseup", mouseup, true);
      
      this.removeEventListener("mouseover", mouseover, true);
      this.removeEventListener("mouseout", mouseout, true);
    }
    
    window.addEventListener("keydown", keydown, true);
    window.addEventListener("mousemove", mousemove, true);
    window.addEventListener("touchend", touchend, true);
    window.addEventListener("touchmove", touchmove, {captured : true, passive : false});
    window.addEventListener("touchcancel", touchcancel, true);
    window.addEventListener("mouseup", mouseup, true);

    this.addEventListener("mouseover", mouseover, true);
    this.addEventListener("mouseout", mouseout, true);
    //*/
  }
  
  updateName() {
    let name = this.getAttribute("name");
    
    if (name !== this._name) {
      this._name = name;
      let dpi = UIBase.getDPI();
      
      let ts = this.getDefault("DefaultTextSize");
      let tw = ui_base.measureText(this._genLabel(), this.dom, this.g).width/dpi + ts*2;
      
      let w = this.getDefault("numslider_width");
      
      w = Math.max(w, tw);
      this.dom.style["width"] = w+"px";
      
      this._repos_canvas();
      this._redraw();
    }
  }
  
  _genLabel() {
    let val = this.value;
    let text;

    if (val === undefined) {
      text = "error";
    } else {
      val = val === undefined ? 0.0 : val;
      val = val.toFixed(3);

      text = val;
      if (this._name) {
        text = this._name + ": " + text;
      }
    }

    return text;
  }
  
  _redraw() {
    let g = this.g;
    let canvas = this.dom;

    //console.log("numslider draw");
    
    let dpi = UIBase.getDPI();
    let disabled = this.disabled; //this.hasAttribute("disabled");
    
    ui_base.drawRoundBox(this, this.dom, this.g, undefined, undefined, undefined, undefined, disabled ? this.getDefault("DisabledBG") : undefined);
    
    let r = this.getDefault("BoxRadius") * dpi;
    let pad = this.getDefault("BoxMargin") * dpi;
    let ts = this.getDefault("DefaultTextSize");
    
    //if (this.value !== undefined) {
    let text = this._genLabel();
   
    let tw = ui_base.measureText(text, this.dom, this.g).width;
    let cx = this.dom.width/2 - tw/2;
    let cy = this.dom.height/2;
    
    ui_base.drawText(cx, cy + ts/2, text, this.dom, this.g);
    //}
    
    g.fillStyle = "rgba(0,0,0,0.1)";
    
    let d = 7, w=canvas.width, h=canvas.height;
    let sz = 10*dpi;
    
    g.beginPath();
    g.moveTo(d, h*0.5);
    g.lineTo(d+sz, h*0.5 + sz*0.5);
    g.lineTo(d+sz, h*0.5 - sz*0.5);
    
    g.moveTo(w-d, h*0.5);
    g.lineTo(w-sz-d, h*0.5 + sz*0.5);
    g.lineTo(w-sz-d, h*0.5 - sz*0.5);
    
    g.fill();
  }
  
  static define() {return {
    tagname : "numslider-x",
    style : "numslider"
  };}
}
UIBase.register(NumSlider);

