export class Menu extends UIBase {
    constructor() {
      super();
      
      this.items = [];
      
      this.itemindex = 0;
      this.closed = false;
      this.activeItem = undefined;
      
      //we have to make a container for any submenus to
      this.container = document.createElement("span");

