/*
          place-items: start start;
          flex-wrap : nowrap;
          align-content : start;
          place-content : start;
          justify-content : start;
          
          align-items : start;
          place-items : start;
          justify-items : start;
*/        
      let style = document.createElement("style");
      style.textContent = `
        .menucon {
          position:absolute;
          float:left;
          
          display: block;
          -moz-user-focus: normal;
        }
        
        ul.menu {
          display : block;
          
          margin : 0px;
          padding : 0px;
          border-style : solid;
          border-width : 1px;
          border-color: grey;
          -moz-user-focus: normal;
          background-color: ${this.getDefault("MenuBG")};
        }
        
        .menuitem {
          display : block;
          
          list-style-type:none;
          -moz-user-focus: normal;
          
          margin : 0;
          padding : 0px;
          padding-right: 2px;
          padding-left: 4px;
          padding-top : 0px;
          padding-bottom : 0px;
          font : ${ui_base._getFont()};
          background-color: ${this.getDefault("MenuBG")};
        }
        
        .menuitem:focus {
          background-color: rgba(155, 220, 255, 1.0);
          -moz-user-focus: normal;
        }
      `;
      
      this.dom.setAttribute("tabindex", -1);
      
      this.dom.addEventListener("keydown", (e) => {
        console.log("key", e.keyCode);
        
        switch (e.keyCode) {
          case 38: //up
          case 40: //down
            let item = this.activeItem;
            if (!item) {
              item = this.dom.childNodes[0];
            }
            if (!item) {
              return;
            }
            
            console.log(item);
            let item2;
            
            if (e.keyCode == 38) {
              item2 = item.previousElementSibling;
            } else {
              item2 = item.nextElementSibling;
            }
            console.log("item2:", item2);
            
            if (item2) {
              this.activeItem = item2;
              
              item.blur();
              item2.focus();
            }
            break;
          case 13: //return key
          case 32: //space key
            this.click(this.activeItem);
          case 27: //escape key
            this.close();
        }
      }, false);
      
      this.container.addEventListener("mouseleave", (e) => {
        console.log("menu out");
        this.close();
      }, false);
      
      //this.container.appendChild(this.dom);
      
